var yaml = require('js-yaml');
var fs = require('fs');
var path = require('path');

fs.readdirSync(path.resolve(__dirname, 'templates')).forEach(function (file) {
    var doc = yaml.safeLoad(
        fs.readFileSync(path.resolve(__dirname, 'templates', file), 
        { encoding: 'utf8' }));
    exports[doc.auth0ExtensionName] = doc;
});
